mapper.insert:
DECLARE
pname T_HRSYS_DEPT.DEPT_NAME%TYPE ;
AS
BEGIN
  SELECT DEPT_NAME INTO panme FROM T_HRSYS_DEPT WHERE  DEPT_NAME=#{deptName};
  IF pname is null then
  INSERT INTO T_HRSYS_DEPT (DEPT_NAME, DEPT_BUILD_TIME, DEPT_STATUS) VALUES (#{deptName},#{deptBuild},#{deptStatus});
  end if;
END ;

mapper.select:
{call PAGING_CURSOR(
#{tableName, mode=IN,jdbcType=VARCHAR},
#{sqlWhere, mode=IN,jdbcType=VARCHAR},
#{pageSize, mode=IN,jdbcType=INTEGER},
#{pageNow, mode=IN,jdbcType=INTEGER},
#{result, mode=OUT,jdbcType=CURSOR,javaType=java.sql.ResultSet,resultMap=findDeptMap},
#{rows, mode=OUT,jdbcType=INTEGER},
#{pageCount, mode=OUT,jdbcType=INTEGER}
)}
